LiquidPart.Liquid:
{% assign projects = Queries.PortfolioSummaryQuery | query %}

<div class="row">
{% for project in projects %}
	 <div class="col-lg-4 portfolio-item">
          <div class="card h-100">
            {% a display_for:project %}
            <img class="card-img-top" src="{{ project.Content.Project.Image.Paths | asset_url }}" alt="">
            {% enda %}
            <div class="card-body">
              {% a display_for:project %}
              <h4 class="card-title">
                {{ project.Content.TitlePart.Title }}
              </h4>
              {% enda %}
              <p class="card-text">{{ project.Content.HtmlBodyPart.Html }}</p>
            </div>
          </div>
        </div>
{% endfor %}
</div>

{% assign customerParent = Content["alias:customers"].Content %}
{% assign customers = Queries.CustomersQuery | query %}

<h2>{{ customerParent.TitlePart.Title }}</h2>
<div class="row">
{% for customer in customers %}
  <div class="col-lg-2 col-sm-4 mb-4">
     <img class="img-fluid" src="{{ customer.Content.Customer.Logo.Paths[0] | asset_url }}" alt="">
  </div>
{% endfor %}
 </div>

{% assign path = "slug:" + Request.Path  %}
{% assign currentItem = Content[path]  %}

<div class="container">
            <h1 class="mt-4 mb-3">
                {{ currentItem.Content.TitlePart.Title }}
                <small>{{ currentItem.Content.SubtitlePart.Subtitle.Text }}</small>
            </h1>
            <ol class="breadcrumb">
                <li class="breadcrumb-item">
                   <a href="{{ "~/" | href }}">Home</a>
                </li>
              {% assign breadcrumb = currentItem.Content.AutoroutePart.Path | split: "/" %}
              {% assign allItems = breadcrumb | size - 1 %}
              
              {% for i in (0..allItems) %}
              
              	{% if i == allItems %}
              
              		<li class="breadcrumb-item active">{{ breadcrumb[i]  | capitalize }}</li>
              	{% else %}
              		{% assign baseUrl = "~/" + breadcrumb[i] %}
              		<li class="breadcrumb-item"><a href="{{ baseUrl | href }}">{{ breadcrumb[i] | capitalize }}</a></li> 
              	{% endif %}
              
              {% endfor %}
            </ol>
        </div>
<hr />

{% assign blogposts = Queries.BlogPostQuery | query %}

{% for post in blogposts %}
	<div class="card mb-4">
        <div class="card-body">
          <div class="row">
            <div class="col-lg-6">
              <a href="#">
                <img class="img-fluid rounded" src="{{ post.Content.BlogPost.Image.Paths | asset_url }}" alt="">
              </a>
            </div>
            
            <div class="col-lg-6">
              {% a display_for:post %}
              <h2 class="card-title">{{ post.Content.TitlePart.Title }}</h2>
              {% enda %}
              <p class="card-text">{{ post.Content.MarkdownBodyPart.Markdown | markdownify | truncatewords: 30 |raw }}</p>
              {% a display_for:post %}
              <span class="btn btn-primary">Read More &rarr;</span>
				{% enda %}
            </div>
          </div>
        </div>
        <div class="card-footer text-muted">
          Posted on {{ post.ContentItem.PublishedUtc | date: "%b %d, %y" }} by {{ post.ContentItem.Author }}
        </div>
      </div>
{% endfor %}

{% assign faq = Queries.QueryFAQ | query %}
{% assign num = 1 %}
{% assign class = "" %}
<div class="mb-4" id="accordion" role="tablist" aria-multiselectable="true">
{% for question in faq %}
		<div class="card">
          <div class="card-header" role="tab" id="heading{{ num }}">
            <h5 class="mb-0">
              <a data-toggle="collapse" data-parent="#accordion" href="#collapse{{ num }}" aria-controls="collapse{{ class }}">{{ question.Content.TitlePart.Title }}</a>
            </h5>
          </div>
          <div id="collapse{{ num }}" class="collapse" role="tabpanel" aria-labelledby="heading{{ num }}">
            <div class="card-body">
             {{ question.Content.HtmlBodyPart.Html }}
            </div>
          </div>
        </div>
  {% assign num = num + 1 %}
{% endfor %}
</div>
